import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Token } from 'src/model/models';
import { TokenService } from 'src/service/token-service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent {
  username = '';
  password = '';

  constructor(private tokenService: TokenService, private router: Router) {
    console.log('LoginComponent created!');
  }
  onSubmit() {
    this.tokenService.login(this.username, this.password).subscribe(
      result => {
        const token = result;
        console.log('Login successful!', result);
        localStorage.setItem('token', token.token);
        this.router.navigate(['/user-list']);
      },
      (error) => {
        console.log('Login failed:', error);
      }
    );
  }
}
<div class="container">
  <form (submit)="onSubmit()">
    <h3>Login</h3>
    <div class="form-group">
      <label>Username</label>
      <input type="text" class="form-control" [(ngModel)]="username" name="username" required>
    </div>
    <div class="form-group">
      <label>Password</label>
      <input type="password" class="form-control" [(ngModel)]="password" name="password" required>
    </div>
    <button type="submit" class="btn btn-primary">Login</button>
  </form>
</div>
